import os
from dotenv import load_dotenv
from langchain_openai import OpenAIEmbeddings
load_dotenv(verbose=True, override=True)


siliconflow_api_key = os.environ["SILLICONFLOW_API_KEY"]
siliconflow_base_url = os.environ["SILLICONFLOW_API_BASE"]

embedding_model = OpenAIEmbeddings(
    model="netease-youdao/bce-embedding-base_v1",
    openai_api_key=siliconflow_api_key,
    openai_api_base=siliconflow_base_url,
)

embeddings = embedding_model.embed_documents([
    "嗨，你好",
    "哈喽，你好",
    "你叫什么名字",
    "我的朋友叫我胖虎",
    "hello 胖虎"
])

embeddings_len = len(embeddings)
# 打印嵌入向量的数量
print(f"embeddings: {embeddings_len}\n")
# 每个嵌入向量的维度是多少（也叫嵌入维度 / embedding dimension）
print(f"embeddings[0] length: {len(embeddings[0])}")


# 使用你指定的嵌入模型，将字符串 "我的朋友叫我胖虎" 转换成一个向量（embedding）。
query_embeddings = embedding_model.embed_query("我的朋友叫我胖虎")
# 打印这个向量。这是一个表示你这句中文在语义空间中的“位置”的向量，向量之间可以用余弦相似度等方法比较“语义相似度”。
print(f"query_embeddings: {query_embeddings}")

